mapper.insert:
INSERT INTO survey (
    member_id, country, area, 
    start_date, end_date, stay_type, 
    peoplecount, climate
) VALUES (
    #{member_id}, #{country}, #{area}, 
    #{start_date}, #{end_date}, #{stay_type}, 
    #{peoplecount}, #{climate}
);

INSERT INTO activity_type (
    survey_no, activity_type
) VALUES (
    #{survey_no}, #{activity_type}
);
